import { FileProtectOutlined, SearchOutlined } from "@ant-design/icons";
import { useContext, useState } from "react";
import { useSearch } from "../api";

import { Button, Image, Input, Space } from "antd";
import { LoadingContext, ProductsContext } from "../context";

import { Store } from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import "/src/assets/img.scss";

const Nav = () => {
  const { setProducts } = useContext(ProductsContext);
  const { setLoading } = useContext(LoadingContext);
  const { getImages } = useSearch();

  const [photoUrl, setPhotoUrl] = useState("");
  const [token, setToken] = useState("");

  const [imageSrc, setImageSrc] = useState("");
  const [errorImg, setErrorImg] = useState(false);

  const handleClick = async () => {
    const searchImg = imageSrc !== "" ? imageSrc : photoUrl;
    setLoading(true);
    const items = await getImages(token, searchImg);
    // fetch("/src/data.json")
    //   .then((response) => response.json())
    //   .then((items) => setProducts(items.result.items));

    if (items instanceof Error) {
      Store.addNotification({
        // title: `Ошибка ${items.response.status}`,
        title: `Ошибка`,
        message: "Ошибка запроса",
        type: "danger",
        insert: "top",
        container: "top-left",
        animationIn: ["animate__animated", "animate__fadeIn"],
        animationOut: ["animate__animated", "animate__fadeOut"],
        dismiss: {
          duration: 5000,
          onScreen: true,
        },
      });
    } else {
      setProducts(items);
    }
    setLoading(false);
  };

  const pasteHandler = async (e) => {
    const clipboardItems = await navigator.clipboard.read();

    for (const clipboardItem of clipboardItems) {
      const imageTypes = clipboardItem.types.find((type) =>
        type.startsWith("image/")
      );

      if (imageTypes) {
        const blob = await clipboardItem.getType(imageTypes);
        const url = URL.createObjectURL(blob);
        setImageSrc(url);
        setErrorImg(false);
        break;
      } else {
        const url = e.target.value;
        setImageSrc(url);
        setErrorImg(false);
      }
    }
  };

  const errorImgHandler = () => {
    setErrorImg(true);
  };

  return (
    <>
      <div className="navbar-container">
        <div className="img-preview">
          {imageSrc ? (
            <img
              src={imageSrc}
              className={errorImg ? "img-error" : ""}
              alt="Ошибка вставки"
              height="100%"
              onError={errorImgHandler}
            />
          ) : (
            <Image
              height={200}
              src="error"
              fallback="data:image/png;base64,"
            />
          )}
        </div>
        <div className="token-search">
          <Input
            prefix={<FileProtectOutlined />}
            placeholder="Введите токен..."
            onChange={(e) => setToken(e.target.value)}
          />
        </div>

        <div className="img-search">
          <Space.Compact style={{ width: "100%" }}>
            <Input
              prefix={<SearchOutlined />}
              placeholder="Введите ссылку на фото либо вставьте скопированное фото..."
              onChange={(e) => setPhotoUrl(e.target.value)}
              onPaste={(e) => pasteHandler(e)}
            />
            <Button onClick={() => handleClick()} type="primary">
              Найти
            </Button>
          </Space.Compact>
        </div>
      </div>
    </>
  );
};

export default Nav;
